import React, {useState, useEffect, useContext} from "react"
import PropTypes from "prop-types"
import {Link} from "react-router-dom"
import Particles from "react-particles-js"
import {CSSTransition} from "react-transition-group"
import {withRouter} from "react-router"

import {Button} from "semantic-ui-react"
import {Container} from "semantic-ui-react"
import {Header} from "semantic-ui-react"
import {Divider} from "semantic-ui-react"
import SpotifyLogin from "react-spotify-login"
import {clientId, redirectUri} from "./settings"
import {AuthContext} from "../AuthContext"

import "../GlobalStyle.css"

const onSuccess = response => console.log(response)
const onFailure = response => console.error(response)

const LandingPage = (props) => {
    const {loggedIn, accessToken, setLoggedIn, setToken} = useContext(AuthContext)
    const [isLoaded, setLoaded] = useState(false)

    useEffect(() => {
        setLoaded(true)
    })

    return (
        <CSSTransition classNames='fade' timeout={{enter: 500, exit: 300}} display={isLoaded}> 
          <Container 
            style={{
              display: "flex", 
              background: "rgb(127,24,161)",
              flexDirection: "column", 
              alignItems: "center", 
              justifyContent: "center", 
              height: "100vh",
            }} 
          >
            <Header as='h1' style={{fontSize: "10rem", margin: "1rem"}}>
              ğ•¾ğ–ˆğ–”ğ–•ğ–ğ–‹ğ–
            </Header>
            <p style={{fontSize: "2rem", margin: "1rem"}}> 
              Horoscoped Playlists 
            </p>
            {!loggedIn ? (
              <Button 
                as={SpotifyLogin} 
                clientId={clientId}
                redirectUri={redirectUri}
                scope={"playlist-modify-public,playlist-modify-private,user-top-read"}
                onSuccess={(response) => {
                  setToken(response.access_token)
                  setLoggedIn(true)
                  console.log(accessToken)
                  console.log(response.access_token)
                }}
                onFailure={onFailure}
                content='Log In to Spotify'/>
            ) : (
              <Button 
                as={ Link }
                onClick={() => setLoaded(false)}
                to='/step1' 
                size='massive'
                style={{margin: "1rem"}} 
                content='Get Started' />
            )}
            <Divider />
        </Container>
      </CSSTransition>
    )
}

LandingPage.propTypes = {
}

export default withRouter(LandingPage)
